import AnnouncementCard1 from "./AnnouncementCard1";




function Announcement() {

    const details = [{
        detail: "Outing schedule for every departement",
        time:"5 Minutes ago" ,
        img: "./images/pinned.png",
    },
    {
        detail: "Meeting HR Department",
        time:"Yesterday, 12:30 PM" ,
        img: "./images/pin.png",
    },
    {
        detail: "IT Department need two more talents for UX/UI Designer position",
        time:"Yesterday, 09:15 AM" ,
        img: "./images/pin.png",
    },
    {
        detail: "IT Department need two more talents for UX/UI",
        time:"Yesterday, 09:15 AM" ,
        img: "./images/pin.png",
    },];

    return(
        <div className="announcement">
            <Atitle/>
            <AnnouncementCards/>
            <BtnAnnouncements/>

        </div>
    )
}

function Atitle() {
    return (
        <div className="atitle">
            <div className="atext">
            Announcement
            </div>
            <div className="dtdropdown">

            </div>
        </div>
    )
}

function BtnAnnouncements() {
    return (
        <div className="btnannouncements">
            <div className="btntext">
            See All Announcement
            </div>
        </div>
    )
}

function AnnouncementCards() {
    return(

        <div className="announcementcards">
           
            {details.map((i, index) => {
                return (
                    <div key={index}>
                        {/* <AnnouncementCard1 title={i.detail} time={i.time} img={i.img}  /> */}
                    </div>
                )
            })}
            
            
        </div>
    )
}

export default Announcement;
